# (@user_id, @user_name)は(@1,@2)の数字の部分に名前がついたのと同じ そのため、csvに列名を記入しても関係ない
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/users.csv' INTO TABLE users FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@user_id, @user_name) SET user_id=@user_id, user_name=@user_name;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/workspaces.csv' INTO TABLE workspaces FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@workspace_id, @workspace_name) SET workspace_id=@workspace_id, workspace_name=@workspace_name;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/workspace_belong_users.csv' INTO TABLE workspace_belong_users FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@workspace_id, @user_id) SET workspace_id=@workspace_id, user_id=@user_id;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/channels.csv' INTO TABLE channels FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@channel_id, @workspace_id, @channel_name) SET channel_id=@channel_id, workspace_id=@workspace_id, channel_name=@channel_name;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/channel_belong_users.csv' INTO TABLE channel_belong_users FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@channel_id, @user_id) SET channel_id=@channel_id, user_id=@user_id;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/messages.csv' INTO TABLE messages FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@message_id, @user_id, @channel_id, @content, @is_send) SET message_id=@message_id, user_id=@user_id, channel_id=@channel_id, content=@content, is_send=@is_send;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/thread_messages.csv' INTO TABLE thread_messages FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@reaction_id, @belong_msg_id) SET owner_msg_id=@owner_msg_id, belong_msg_id=@belong_msg_id;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/reactions.csv' INTO TABLE reactions FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@reaction_id, @user_id, @message_id, @stamp_type) SET reaction_id=@reaction_id, user_id=@user_id, message_id=@message_id, stamp_type=@stamp_type;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/mentions.csv' INTO TABLE mentions FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@mention_id, @message_id) SET mention_id=@mention_id, message_id=@message_id;"
mysql -u${MYSQL_ROOT_USER} -p${MYSQL_ROOT_PASSWORD} praha_slack -e "LOAD DATA LOCAL INFILE '/docker-entrypoint-initdb.d/mention_send_users.csv' INTO TABLE mention_send_users FIELDS TERMINATED BY ',' ENCLOSED BY '\"' IGNORE 1 LINES (@mention_id, @user_id) SET mention_id=@mention_id, user_id=@user_id;"